func (dao *MongoSQSDAO) ListQueues(ctx context.Context, reportTime time.Time, accountID, region *string, limit, offset *int64) ([]*sqs.Queue, error) {
	filter := bson.D{
		bson.E{"report_time", reportTime},
	}
	if accountID != nil {
		filter = append(filter, bson.E{Key: "account_id", Value: *accountID})
	}
	if region != nil {
		filter = append(filter, bson.E{Key: "region", Value: *region})
	}

	var results []*sqs.Queue
	cursor, err := dao.db.Collection("aws_sqs_queues").Find(ctx, filter)
	if err != nil {
		return nil, err
	}

	err = cursor.All(ctx, &results)
	if err != nil {
		return nil, err
	}

	return results, nil
}

func (dao *MongoSQSDAO) GetQueue(ctx context.Context, reportTime time.Time, id string) (*sqs.Queue, error) {
	filter := bson.D{
		bson.E{"report_time", reportTime},
		bson.E{"queue_arn", id},
	}

	var result *sqs.Queue
	err := dao.db.Collection("aws_sqs_queues").FindOne(ctx, filter).Decode(&result)

	return result, err
}

func (dao *MongoSQSDAO) PutQueues(ctx context.Context, resources []*sqs.Queue) error {
	if len(resources) == 0 {
		return nil
	}
	writes := make([]interface{}, len(resources))
	for i, resource := range resources {
		writes[i] = resource
	}
	_, err := dao.db.Collection("aws.sqs.queues").InsertMany(ctx, writes)

	return err
}

