func FetchTables(ctx context.Context, params *localAws.AwsFetchInput) ([]*Table, *localAws.AwsFetchOutputMetadata) {
	fetchingErrors := []error{}
	var fetchedResources int
	var failedResources int
	inventoryResults := &meta.InventoryResults{
		Cloud:      "aws",
		Service:    "dynamodb",
		Resource:   "tables",
		AccountId:  params.AccountId,
		Region:     params.Region,
		ReportTime: params.ReportTime,
	}
	resources := []*Table{}

	awsClient := params.RegionalClients[params.Region]
	client := awsClient.DynamoDB()

	paginator := dynamodb.NewListTablesPaginator(client, &dynamodb.ListTablesInput{})

	for paginator.HasMorePages() {
		output, err := paginator.NextPage(ctx)

		if err != nil {
			fetchingErrors = append(fetchingErrors, fmt.Errorf("error calling ListTables in %s/%s: %w", params.AccountId, params.Region, err))
			break
		}

		for _, tableName := range output.TableNames {
			result, err := client.DescribeTable(ctx, &dynamodb.DescribeTableInput{
				TableName: aws.String(tableName),
			})
			if err != nil {
				fetchingErrors = append(fetchingErrors, fmt.Errorf("error calling DescribeTable on %s in %s/%s: %w", tableName, params.AccountId, params.Region, err))
				continue
			}

			tagsResult, err := client.ListTagsOfResource(ctx, &dynamodb.ListTagsOfResourceInput{
				ResourceArn: result.Table.TableArn,
			})
			if err != nil {
				fetchingErrors = append(fetchingErrors, fmt.Errorf("error calling ListTagsOfResource on %s in %s/%s: %w", tableName, params.AccountId, params.Region, err))
				continue
			}

			model := new(Table)
			copier.CopyWithOption(&model, &result.Table, localAws.CopyOption)

			model.Tags = ConvertTags(tagsResult.Tags)
			model.AccountId = params.AccountId
			model.Region = params.Region
			model.ReportTime = params.ReportTime
			model.InventoryUUID = uuid.New().String()

			resources = append(resources, model)
			fetchedResources++
		}

	}

	inventoryResults.FetchedResources = fetchedResources
	inventoryResults.FailedResources = failedResources
	inventoryResults.HadErrors = len(fetchingErrors) > 0

	return resources, &localAws.AwsFetchOutputMetadata{
		FetchingErrors:   fetchingErrors,
		InventoryResults: inventoryResults,
	}
}
