func FetchFileSystemDescription(ctx context.Context, params *awscloud.AwsFetchInput) *awscloud.AwsFetchOutput {
	fetchingErrors := []error{}
	var fetchedResources int
	var failedResources int
	inventoryResults := &meta.InventoryResults{
		Cloud: "aws",
		Service: "efs",
		Resource: "filesystems",
		AccountId: params.AccountId,
		Region: params.Region,
		ReportTime: params.ReportTime.UTC().UnixMilli(),
	}

	awsClient := params.RegionalClients[params.Region]
	client := awsClient.EFS()

	
	paginator := efs.NewDescribeFileSystemsPaginator(client, &efs.DescribeFileSystemsInput{})

	for paginator.HasMorePages() {
		output, err := paginator.NextPage(ctx)
	
		if err != nil {
			fetchingErrors = append(fetchingErrors, fmt.Errorf("error calling DescribeFileSystems in %s/%s: %w", params.AccountId, params.Region, err))
			break
		}

		for _, object := range output.FileSystems {

			model := new(FileSystemDescription)
			copier.Copy(&model, &object)

			model.Tags = ConvertTags(object.Tags)
			model.AccountId = params.AccountId
			model.Region = params.Region
			model.ReportTime = params.ReportTime.UTC().UnixMilli()

			
			if err = PostProcessFileSystemDescription(ctx, params, model); err != nil {
				fetchingErrors = append(fetchingErrors, fmt.Errorf("error post-processing FileSystemDescription %s %s/%s: %w", model.FileSystemId, params.AccountId, params.Region, err))
				failedResources++
			}
			

			err = params.OutputFile.Write(ctx, model)
			if err != nil {
				fetchingErrors = append(fetchingErrors, fmt.Errorf("error storing FileSystemDescription model in %s/%s: %w", params.AccountId, params.Region, err))
			}
			fetchedResources++
		}

	}

	inventoryResults.FetchedResources = fetchedResources
	inventoryResults.FailedResources = failedResources
	inventoryResults.HadErrors = len(fetchingErrors) > 0

	return &awscloud.AwsFetchOutput{
		FetchingErrors:   fetchingErrors,
		InventoryResults: inventoryResults,
		ResourceName:     "filesystems",
		AccountId:        params.AccountId,
		Region:           params.Region,
	}
}
